-- Inventory flight (mirror from pack plan items)

SELECT pp.flight_id, ppi.item_id, SUM(ppi.quantity_units) AS qty, 'ONBOARD'
FROM pack_plans pp
JOIN pack_plan_items ppi ON ppi.pack_plan_id = pp.id
GROUP BY pp.flight_id, ppi.item_id
ON DUPLICATE KEY UPDATE quantity_units=VALUES(quantity_units), location=VALUES(location);


